package io.jsd.training.designpattern.book.chap3.Meteo.meteo;public class DonneesMeteo extends Sujet {    protected Double temperature;    protected Double vitesseVent;    protected Double tauxHumidite;    public Double getTemperature() {        return temperature;    }    public void setTemperature(Double temperature) {        this.temperature = temperature;        notifie();    }    public Double getVitesseVent() {        return vitesseVent;    }    public void setVitesseVent(Double vitesseVent) {        this.vitesseVent = vitesseVent;        notifie();    }    public Double getTauxHumidite() {        return tauxHumidite;    }    public void setTauxHumidite(Double tauxHumidite) {        this.tauxHumidite = tauxHumidite;        notifie();    }}