package io.jsd.training.designpattern.book.chap3.Meteo.meteoobservable;import java.util.Observable;public class DonneesMeteo extends Observable {    protected Double temperature;    protected Double vitesseVent;    protected Double tauxHumidite;    public Double getTemperature() {        return temperature;    }    public void setTemperature(Double temperature) {        this.temperature = temperature;        setChanged();        notifyObservers();    }    public Double getVitesseVent() {        return vitesseVent;    }    public void setVitesseVent(Double vitesseVent) {        this.vitesseVent = vitesseVent;        setChanged();        notifyObservers();    }    public Double getTauxHumidite() {        return tauxHumidite;    }    public void setTauxHumidite(Double tauxHumidite) {        this.tauxHumidite = tauxHumidite;        setChanged();        notifyObservers();    }}