package io.jsd.training.java.designpattern.book.chap3.ObjetsGraphiques.objetsgraphiquesavecannule;public class InterfaceGraphique {    public static void main(String[] args) {        FormeGraphique forme = new FormeGraphique(0, 3);        ZoneTexte texte = new ZoneTexte();        Appelant appelant = new Appelant();        Commande formeHaut1 = new CommandeFlecheHautFG(                forme);        appelant.setCommande(formeHaut1);        appelant.execute();        Commande formeHaut2 = new CommandeFlecheHautFG(                forme);        appelant.setCommande(formeHaut2);        appelant.execute();        System.out                .println("la position verticale de la forme graphique est: "                        + forme.getY());        Commande texteBas = new CommandeFlecheBasZT(texte);        appelant.setCommande(texteBas);        appelant.execute();        System.out                .println("la taille de la police du texte est: "                        + texte.getTaillePolice());        appelant.annule();        appelant.annule();        System.out                .println("la position verticale de la forme graphique est: "                        + forme.getY());        System.out                .println("la taille de la police du texte est: "                        + texte.getTaillePolice());    }}